import React from 'react';
import {useSelector} from 'react-redux';
import {Link} from 'react-router-dom';
import {selectUserById, selectAllUsers} from './usersSlice';
import {selectAllPosts, selectPostById} from '../posts/postsSlice';

export const UserPage = ({match}) =>{
    const {userId} = match.params

    const user = useSelector( state => selectPostById(state, userId))

    const postsForUser = useSelector(state =>{
        const allPosts = selectAllPost(state)
        return allPosts.filter(post => 
            post.user ===userId
            )
    })

    const postTitles = postsForUser.map(post =>(
        <li key ={post.id}>
            <Link to={`/post/${post.id}`}> {post.title}</Link>
        </li>
    ))

    return(
        <section>
            <h2>{user.name}</h2>
            <ul> {postTitles}</ul>
        </section>
    )
}
